    private void addStory(final ArrayList<AllStoryFormat> arrayList) {
        size = new int[]{1};
        firestore.collection("allStory").get().addOnCompleteListener(new OnCompleteListener<QuerySnapshot>() {
            @Override
            public void onComplete(@NonNull Task<QuerySnapshot> task) {
                QuerySnapshot querySnapshot = task.getResult();
                if (querySnapshot != null) {
                    size[0] = querySnapshot.size() + 1;
                }
                Log.d("size", "onComplete: " + size[0]);
//                id[0] = FirebaseFirestore.getInstance().collection("id").document().getId();

                for (int j = 0; j < arrayList.size(); j++) {
                    batch.set(firestore.collection("allStory").document(String.valueOf(size[0]+j)), arrayList.get(j));
                    for (int i = 0; i < 10; i++) {
                        String uid = FirebaseFirestore.getInstance().collection("id").document().getId();
                        batch.set(firestore.collection("allProduct").document(
                                        String.valueOf(size[0]+j)).collection("productData").document()
                                , new ProductFormat(arrayList.get(j).getStoryPath(), uid, false, null)
                        );
                    }
                }
                batch.commit().addOnCompleteListener(new OnCompleteListener<Void>() {
                    @Override
                    public void onComplete(@NonNull Task<Void> task) {
                        if (task.isSuccessful()) {
                            Log.d("TAG", "onComplete: " + "is woooooooooke");
                        }
                    }
                }).addOnFailureListener(new OnFailureListener() {
                    @Override
                    public void onFailure(@NonNull Exception e) {
                        Log.d("TAG", "onComplete: " + "is Failure");
                    }
                });
            }
        });
    }
